{
    "version" : "2017-02-28",
    "operation" : "PutItem",
    "key": {
      "gameID": $util.dynamodb.toDynamoDBJson($ctx.args.gameID)
    },
    "attributeValues": $util.dynamodb.toMapValuesJson({
      "title": "Join the game!",
      "text": "Open RightOn and enter the code below to join the game.",
      "screenID": "JoinScreen"
    })
}

{
  "version": "2018-05-29",
  "statements":   ["SELECT id, title, grade, domain, cluster, description, standard, image_url, phase_one_time, phase_two_time, to_char(created_at, 'YYYY-MM-DD\"T\"HH24:MI:SS\"Z\"') AS created_date_time, to_char(updated_at, 'YYYY-MM-DD\"T\"HH24:MI:SS\"Z\"') AS updated_date_time FROM games WHERE id = :ID", "DELETE FROM games WHERE id = :ID"],
  "variableMap": {
  	":ID": $util.toJson($ctx.args.id)
  }
}

{
  "version": "2018-05-29",
  "statements":   ["SELECT *, to_char(created_at, 'YYYY-MM-DD\"T\"HH24:MI:SS\"Z\"') AS created_date_time, to_char(updated_at, 'YYYY-MM-DD\"T\"HH24:MI:SS\"Z\"') AS updated_date_time FROM questions WHERE id = :ID", "DELETE FROM questions WHERE id = :ID"],
  "variableMap": {
  	":ID": $util.toJson($ctx.args.id)
  }
