export const createClassroom: "\n  mutation CreateClassroom(\n    $input: CreateClassroomInput!\n    $condition: ModelClassroomConditionInput\n  ) {\n    createClassroom(input: $input, condition: $condition) {\n      id\n      learningGaps {\n        items {\n          id\n          classroomId\n          title\n          priority\n          studentCount\n          studentPercent\n          occurrence\n          misconceptionSummary\n          successIndicators\n          evidence {\n            source\n            mostCommonError\n            sampleStudentWork\n            aiThinkingPattern\n            __typename\n          }\n          move {\n            id\n            title\n            time\n            format\n            summary\n            aiReasoning\n            tabs {\n              __typename\n            }\n            __typename\n          }\n          createdAt\n          updatedAt\n          classroomLearningGapsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      recommendedActions {\n        items {\n          id\n          classroomId\n          priority\n          title\n          description\n          action\n          timeframe\n          affectedStudents\n          dataSource\n          remediationType\n          ccssStandard\n          targetObjectives {\n            standard\n            description\n            __typename\n          }\n          prerequisiteGaps {\n            standard\n            description\n            gapDescription\n            __typename\n          }\n          createdAt\n          updatedAt\n          classroomRecommendedActionsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      students {\n        items {\n          id\n          classroomId\n          performanceX\n          performanceY\n          confidenceLevel\n          status\n          createdAt\n          updatedAt\n          classroomStudentsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      trends {\n        items {\n          keyInsights {\n            finding\n            targetStandard\n            targetDescription\n            gapStandard\n            gapDescription\n            __typename\n          }\n          studentPerformanceGroups {\n            highPerformers {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            averagePerformers {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            strugglingStudents {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            biggestChanges {\n              name\n              masteryChange\n              misconceptionChange\n              topic\n              __typename\n            }\n            __typename\n          }\n          confidenceIssues {\n            overlyConfident {\n              name\n              topic\n              __typename\n            }\n            underlyConfident {\n              name\n              topic\n              __typename\n            }\n            __typename\n          }\n          id\n          createdAt\n          updatedAt\n          classroomTrendsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      createdAt\n      updatedAt\n      __typename\n    }\n  }\n";
