export const getLearningScience: "\n  mutation GetLearningScience($input: GetLearningScienceInput!) {\n    getLearningScience(input: $input)\n  }\n";
export const createClassroom: "\n  mutation CreateClassroom(\n    $input: CreateClassroomInput!\n    $condition: ModelClassroomConditionInput\n  ) {\n    createClassroom(input: $input, condition: $condition) {\n      id\n      userName\n      sessions {\n        items {\n          id\n          classroomId\n          question\n          correctAnswer\n          steps\n          incorrectAnswer1\n          incorrectAnswer1Explanation\n          incorrectAnswer2\n          incorrectAnswer2Explanation\n          incorrectAnswer3\n          incorrectAnswer3Explanation\n          createdAt\n          updatedAt\n          classroomSessionsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      createdAt\n      updatedAt\n      __typename\n    }\n  }\n";
export const updateClassroom: "\n  mutation UpdateClassroom(\n    $input: UpdateClassroomInput!\n    $condition: ModelClassroomConditionInput\n  ) {\n    updateClassroom(input: $input, condition: $condition) {\n      id\n      userName\n      sessions {\n        items {\n          id\n          classroomId\n          question\n          correctAnswer\n          steps\n          incorrectAnswer1\n          incorrectAnswer1Explanation\n          incorrectAnswer2\n          incorrectAnswer2Explanation\n          incorrectAnswer3\n          incorrectAnswer3Explanation\n          createdAt\n          updatedAt\n          classroomSessionsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      createdAt\n      updatedAt\n      __typename\n    }\n  }\n";
