export const getClassroom: "\n  query GetClassroom($id: ID!) {\n    getClassroom(id: $id) {\n      id\n      userName\n      sessions {\n        items {\n          id\n          classroomId\n          question\n          correctAnswer\n          steps\n          incorrectAnswer1\n          incorrectAnswer1Explanation\n          incorrectAnswer2\n          incorrectAnswer2Explanation\n          incorrectAnswer3\n          incorrectAnswer3Explanation\n          createdAt\n          updatedAt\n          classroomSessionsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      createdAt\n      updatedAt\n      __typename\n    }\n  }\n";
export const listClassrooms: "\n  query ListClassrooms(\n    $filter: ModelClassroomFilterInput\n    $limit: Int\n    $nextToken: String\n  ) {\n    listClassrooms(filter: $filter, limit: $limit, nextToken: $nextToken) {\n      items {\n        id\n        userName\n        sessions {\n          nextToken\n          __typename\n        }\n        createdAt\n        updatedAt\n        __typename\n      }\n      nextToken\n      __typename\n    }\n  }\n";
export const getClassroomSession: "\n  query GetClassroomSession($id: ID!) {\n    getClassroomSession(id: $id) {\n      id\n      classroomId\n      question\n      correctAnswer\n      steps\n      incorrectAnswer1\n      incorrectAnswer1Explanation\n      incorrectAnswer2\n      incorrectAnswer2Explanation\n      incorrectAnswer3\n      incorrectAnswer3Explanation\n      studentAnswer {\n        items {\n          id\n          classroomSessionId\n          studentId\n          answer\n          explanation\n          confidenceLevel\n          createdAt\n          updatedAt\n          classroomSessionStudentAnswerId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      createdAt\n      updatedAt\n      classroomSessionsId\n      __typename\n    }\n  }\n";
export const listClassroomSessions: "\n  query ListClassroomSessions(\n    $filter: ModelClassroomSessionFilterInput\n    $limit: Int\n    $nextToken: String\n  ) {\n    listClassroomSessions(\n      filter: $filter\n      limit: $limit\n      nextToken: $nextToken\n    ) {\n      items {\n        id\n        classroomId\n        question\n        correctAnswer\n        steps\n        incorrectAnswer1\n        incorrectAnswer1Explanation\n        incorrectAnswer2\n        incorrectAnswer2Explanation\n        incorrectAnswer3\n        incorrectAnswer3Explanation\n        studentAnswer {\n          nextToken\n          __typename\n        }\n        createdAt\n        updatedAt\n        classroomSessionsId\n        __typename\n      }\n      nextToken\n      __typename\n    }\n  }\n";
export const getStudentAnswer: "\n  query GetStudentAnswer($id: ID!) {\n    getStudentAnswer(id: $id) {\n      id\n      classroomSessionId\n      studentId\n      answer\n      explanation\n      confidenceLevel\n      createdAt\n      updatedAt\n      classroomSessionStudentAnswerId\n      __typename\n    }\n  }\n";
export const listStudentAnswers: "\n  query ListStudentAnswers(\n    $filter: ModelStudentAnswerFilterInput\n    $limit: Int\n    $nextToken: String\n  ) {\n    listStudentAnswers(filter: $filter, limit: $limit, nextToken: $nextToken) {\n      items {\n        id\n        classroomSessionId\n        studentId\n        answer\n        explanation\n        confidenceLevel\n        createdAt\n        updatedAt\n        classroomSessionStudentAnswerId\n        __typename\n      }\n      nextToken\n      __typename\n    }\n  }\n";
