export const getClassroom: "\n  query GetClassroom($id: ID!) {\n    getClassroom(id: $id) {\n      id\n      learningGaps {\n        items {\n          id\n          classroomId\n          title\n          priority\n          studentCount\n          studentPercent\n          occurrence\n          misconceptionSummary\n          successIndicators\n          evidence {\n            source\n            mostCommonError\n            sampleStudentWork\n            aiThinkingPattern\n            __typename\n          }\n          move {\n            id\n            title\n            time\n            format\n            summary\n            aiReasoning\n            tabs {\n              __typename\n            }\n            __typename\n          }\n          createdAt\n          updatedAt\n          classroomLearningGapsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      recommendedActions {\n        items {\n          id\n          classroomId\n          priority\n          title\n          description\n          action\n          timeframe\n          affectedStudents\n          dataSource\n          remediationType\n          ccssStandard\n          targetObjectives {\n            standard\n            description\n            __typename\n          }\n          prerequisiteGaps {\n            standard\n            description\n            gapDescription\n            __typename\n          }\n          createdAt\n          updatedAt\n          classroomRecommendedActionsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      students {\n        items {\n          id\n          classroomId\n          performanceX\n          performanceY\n          confidenceLevel\n          status\n          createdAt\n          updatedAt\n          classroomStudentsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      trends {\n        items {\n          keyInsights {\n            finding\n            targetStandard\n            targetDescription\n            gapStandard\n            gapDescription\n            __typename\n          }\n          studentPerformanceGroups {\n            highPerformers {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            averagePerformers {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            strugglingStudents {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            biggestChanges {\n              name\n              masteryChange\n              misconceptionChange\n              topic\n              __typename\n            }\n            __typename\n          }\n          confidenceIssues {\n            overlyConfident {\n              name\n              topic\n              __typename\n            }\n            underlyConfident {\n              name\n              topic\n              __typename\n            }\n            __typename\n          }\n          id\n          createdAt\n          updatedAt\n          classroomTrendsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      createdAt\n      updatedAt\n      __typename\n    }\n  }\n";
export const listClassrooms: "\n  query ListClassrooms(\n    $filter: ModelClassroomFilterInput\n    $limit: Int\n    $nextToken: String\n  ) {\n    listClassrooms(filter: $filter, limit: $limit, nextToken: $nextToken) {\n      items {\n        id\n        learningGaps {\n          items {\n            id\n            classroomId\n            title\n            priority\n            studentCount\n            studentPercent\n            occurrence\n            misconceptionSummary\n            successIndicators\n            evidence {\n              source\n              mostCommonError\n              sampleStudentWork\n              aiThinkingPattern\n              __typename\n            }\n            move {\n              id\n              title\n              time\n              format\n              summary\n              aiReasoning\n              __typename\n            }\n            createdAt\n            updatedAt\n            classroomLearningGapsId\n            __typename\n          }\n          nextToken\n          __typename\n        }\n        recommendedActions {\n          items {\n            id\n            classroomId\n            priority\n            title\n            description\n            action\n            timeframe\n            affectedStudents\n            dataSource\n            remediationType\n            ccssStandard\n            targetObjectives {\n              standard\n              description\n              __typename\n            }\n            prerequisiteGaps {\n              standard\n              description\n              gapDescription\n              __typename\n            }\n            createdAt\n            updatedAt\n            classroomRecommendedActionsId\n            __typename\n          }\n          nextToken\n          __typename\n        }\n        students {\n          items {\n            id\n            classroomId\n            performanceX\n            performanceY\n            confidenceLevel\n            status\n            createdAt\n            updatedAt\n            classroomStudentsId\n            __typename\n          }\n          nextToken\n          __typename\n        }\n        trends {\n          items {\n            keyInsights {\n              finding\n              targetStandard\n              targetDescription\n              gapStandard\n              gapDescription\n              __typename\n            }\n            studentPerformanceGroups {\n              __typename\n            }\n            confidenceIssues {\n              __typename\n            }\n            id\n            createdAt\n            updatedAt\n            classroomTrendsId\n            __typename\n          }\n          nextToken\n          __typename\n        }\n        createdAt\n        updatedAt\n        __typename\n      }\n      nextToken\n      __typename\n    }\n  }\n";
export const getStudent: "\n  query GetStudent($id: ID!) {\n    getStudent(id: $id) {\n      id\n      classroomId\n      performanceX\n      performanceY\n      confidenceLevel\n      status\n      createdAt\n      updatedAt\n      classroomStudentsId\n      __typename\n    }\n  }\n";
export const listStudents: "\n  query ListStudents(\n    $filter: ModelStudentFilterInput\n    $limit: Int\n    $nextToken: String\n  ) {\n    listStudents(filter: $filter, limit: $limit, nextToken: $nextToken) {\n      items {\n        id\n        classroomId\n        performanceX\n        performanceY\n        confidenceLevel\n        status\n        createdAt\n        updatedAt\n        classroomStudentsId\n        __typename\n      }\n      nextToken\n      __typename\n    }\n  }\n";
export const getLearningGap: "\n  query GetLearningGap($id: ID!) {\n    getLearningGap(id: $id) {\n      id\n      classroomId\n      title\n      priority\n      studentCount\n      studentPercent\n      occurrence\n      misconceptionSummary\n      successIndicators\n      evidence {\n        source\n        mostCommonError\n        sampleStudentWork\n        aiThinkingPattern\n        __typename\n      }\n      move {\n        id\n        title\n        time\n        format\n        summary\n        aiReasoning\n        tabs {\n          overview {\n            whatStudentsDo\n            whatYouDo\n            importance\n            __typename\n          }\n          activitySteps {\n            setup\n            problem\n            coreActivity\n            discussionQuestions\n            __typename\n          }\n          materials {\n            required\n            optional\n            __typename\n          }\n          studentGroupings {\n            groups {\n              name\n              description\n              students\n              __typename\n            }\n            highFlyers {\n              students\n              description\n              __typename\n            }\n            aiRecommendation\n            __typename\n          }\n          __typename\n        }\n        __typename\n      }\n      createdAt\n      updatedAt\n      classroomLearningGapsId\n      __typename\n    }\n  }\n";
