export const getClassroom: "\n  query GetClassroom($id: ID!) {\n    getClassroom(id: $id) {\n      id\n      classroomName\n      learningGaps {\n        items {\n          id\n          classroomId\n          title\n          priority\n          studentCount\n          studentPercent\n          occurrence\n          misconceptionSummary\n          successIndicators\n          ccssStandards {\n            targetObjective {\n              standard\n              description\n              __typename\n            }\n            impactedObjectives {\n              standard\n              description\n              __typename\n            }\n            prerequisiteGaps {\n              standard\n              description\n              __typename\n            }\n            __typename\n          }\n          evidence {\n            source\n            mostCommonError\n            sampleStudentWork\n            aiThinkingPattern\n            __typename\n          }\n          move {\n            id\n            title\n            time\n            format\n            summary\n            aiReasoning\n            tabs {\n              __typename\n            }\n            __typename\n          }\n          createdAt\n          updatedAt\n          classroomLearningGapsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      savedNextSteps {\n        items {\n          id\n          classroomId\n          status\n          completedAt\n          sortOrder\n          gapGroupId\n          gapGroupTitle\n          targetObjectiveStandard\n          priority\n          studentCount\n          studentPercent\n          occurrence\n          misconceptionSummary\n          successIndicators\n          gaps\n          moveId\n          moveTitle\n          moveTime\n          moveFormat\n          moveSummary\n          aiReasoning\n          evidence {\n            source\n            mostCommonError\n            sampleStudentWork\n            aiThinkingPattern\n            __typename\n          }\n          move {\n            id\n            title\n            time\n            format\n            summary\n            aiReasoning\n            tabs {\n              __typename\n            }\n            __typename\n          }\n          createdAt\n          updatedAt\n          classroomSavedNextStepsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      students {\n        items {\n          id\n          classroomId\n          name\n          performanceX\n          performanceY\n          confidenceLevel\n          status\n          createdAt\n          updatedAt\n          classroomStudentsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      trends {\n        items {\n          keyInsights {\n            finding\n            targetStandard\n            targetDescription\n            gapStandard\n            gapDescription\n            __typename\n          }\n          studentPerformanceGroups {\n            highPerformers {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            averagePerformers {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            strugglingStudents {\n              name\n              masteryScore\n              previousMastery\n              masteryChange\n              misconceptionScore\n              previousMisconception\n              misconceptionChange\n              __typename\n            }\n            biggestChanges {\n              name\n              masteryChange\n              misconceptionChange\n              topic\n              __typename\n            }\n            __typename\n          }\n          confidenceIssues {\n            overlyConfident {\n              name\n              topic\n              __typename\n            }\n            underlyConfident {\n              name\n              topic\n              __typename\n            }\n            __typename\n          }\n          id\n          createdAt\n          updatedAt\n          classroomTrendsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      createdAt\n      updatedAt\n      __typename\n    }\n  }\n";
