export const onCreateClassroom: "\n  subscription OnCreateClassroom(\n    $filter: ModelSubscriptionClassroomFilterInput\n  ) {\n    onCreateClassroom(filter: $filter) {\n      id\n      userName\n      sessions {\n        items {\n          id\n          classroomId\n          question\n          correctAnswer\n          steps\n          incorrectAnswer1\n          incorrectAnswer1Explanation\n          incorrectAnswer2\n          incorrectAnswer2Explanation\n          incorrectAnswer3\n          incorrectAnswer3Explanation\n          studentAnswer {\n            items {\n              id\n              classroomSessionId\n              studentId\n              answer\n              explanation\n              confidenceLevel\n              createdAt\n              updatedAt\n              classroomSessionStudentAnswerId\n              __typename\n            }\n            nextToken\n            __typename\n          }\n          createdAt\n          updatedAt\n          classroomSessionsId\n          __typename\n        }\n        nextToken\n        __typename\n      }\n      analytics\n      createdAt\n      updatedAt\n      __typename\n    }\n  }\n";
